<?php

/** @var \Illuminate\Database\Eloquent\Factory $factory */

use App\Order;
use Faker\Generator as Faker;

$factory->define(Order::class, function (Faker $faker) {
    return [
        'customer_id' => $faker->numberBetween($min = 0, $max = 150),
        'reference' => $faker->company,
        'products'=> json_encode([['product_id'=>4, 'aantal'=>3],['product_id'=>10, 'aantal'=>1]]),
        'type'=> $faker->randomElement($array = array ('leverbon','orderbon')),
        'KM'=> $faker->randomFloat($nbMaxDecimals = 2, $min = 1, $max = 200),
        'hours'=> $faker->randomFloat($nbMaxDecimals = 2, $min = 1, $max = 30),
        'images'=> '',
        'sign_first_name'=> $faker->firstName,
        'sign_last_name'=> $faker->lastName,
        'signiture'=> "data:image/png;base64,",
        'status'=> 1

    ];
});
